插入元素 默认使用尾插法 void linkLast(E e) { //获取last对象的指针 final Node<E> l = last; //创建一个新的节点对象 //传入previous节点，赋值 final Node<E> newNode = new Node<>(l, e, null); //last指针指向新的对象 last = newNode; //判断l指针指向的对象是否为空 if (l == null) //为空说明链表为空，将first指针指向这个新的对象 first = newNode; else //否则将原last对象的last指针指向这个新的对象 l.next = newNode; //链表的大小加一 size++; modCount++;
} 获取元素 //根据下标查找
public E get(int index) { //	判断下标是否合法 checkElementIndex(index); //根据index查值 return node(index).item;
}
//根据index查值
Node<E> node(int index) { //如果下标小于链表的大小除2则从前往后找 if (index < (size >> 1)) { Node<E> x = first; for (int i = 0; i < index; i++) x = x.next; return x; //否则从后往前找 } else { Node<E> x = last; for (int i = size - 1; i > index; i--) x = x.prev; return x; }
} 删除元素 public E remove(int index) { //	判断下标是否合法 checkElementIndex(index); //找到下表为index的节点并移除 return unlink(node(index));
}
//移除
E unlink(Node<E> x) { // assert x != null; final E element = x.item; final Node<E> next = x.next; final Node<E> prev = x.prev; //该节点的prev节点为空说明该节点就是first节点 if (prev == null) { //将first指针指向next节点 first = next; } else { //否则将prev节点的next指针指向next对象 prev.next = next; //该节点的prev对象置空 x.prev = null; } //该节点的prev节点为空说明该节点就是last节点 if (next == null) { //将last指针指向prev节点 last = prev; } else { //否则将next节点的prev指针指向prev对象 next.prev = prev; 该节点的next对象置空 x.next = null; } //该节点的数据置空 x.item = null; //该链表的大小减一 size--; modCount++; return element;
}

